INSERT INTO tbl_chat_message (room_id, sender_id, content, sent_at) VALUES
                                                                        (1, 1, 'TF에 참여하시는 모든 분들을 이 채팅방에 초대했습니다. 내일부터 시작될 AI 챗봇 개발 프로젝트 관련 논의는 여기서 진행할게요!', '2025-08-27 11:00:15'),
                                                                        (1, 1, '시작에 앞서 관련 자료 먼저 공유드립니다. 시간 되실 때 한번씩 읽어주세요.', '2025-08-27 11:00:45'),
                                                                        (1, 6, '네, 초대 감사합니다. 자료 확인해보겠습니다.', '2025-08-27 11:02:02'),
                                                                        (1, 7, '안녕하세요! 드디어 시작이군요. 잘 부탁드립니다.', '2025-08-27 11:02:30'),
                                                                        (1, 3, '자료 양이 꽤 되네요. 오늘 중으로 다 읽어봐야겠다.', '2025-08-27 11:05:20'),
                                                                        (1, 10, '안녕하세요, 정솔루션입니다. 이번 프로젝트 기대가 많이 됩니다!', '2025-08-27 11:08:15'),
                                                                        (1, 1, '네, 궁금한 점 있으시면 편하게 말씀해주세요.', '2025-08-27 11:10:25'),
                                                                        (1, 5, '안녕하세요. 늦게 확인했네요. 잘 부탁드립니다!', '2025-08-27 13:30:00'),
                                                                        (1, 7, '자료 다 읽어봤는데, 특히 벤치마킹한 다른 서비스들 분석 내용이 인상적이네요.', '2025-08-27 15:01:10'),
                                                                        (1, 1, '맞아요. 우리가 어떤 부분에 더 집중해야 할지 방향을 잡는 데 도움이 될 것 같아서 넣어봤습니다.', '2025-08-27 15:02:00'),
                                                                        (1, 6, '기술 스택 관련해서도 미리 좀 생각해봐야겠네요. LangChain이랑 OpenAI API 조합으로 가는게 맞겠죠?', '2025-08-27 16:05:00'),
                                                                        (1, 10, '네, 저도 그 방향이 가장 효율적일 것 같다고 생각합니다.', '2025-08-27 16:05:45'),
                                                                        (1, 1, '좋습니다. 그 부분은 내일 킥오프 미팅 때 더 자세히 논의해보시죠. 다들 오늘 하루 수고하셨습니다!', '2025-08-27 18:00:00');
